// App.js
import React, { useState, useEffect } from "react";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import DownloadButtons from "./DownloadButtons";
import { parseTree} from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";

const App = () => {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState([]);
  const [focusedNodeId, setFocusedNodeId] = useState(null);

  useEffect(() => {
    try {
      const parsed = parseTree(treeText);
      setTreeData(parsed);
    } catch (err) {
      console.error("Parse error:", err);
    }
  }, [treeText]);

  function findNodeById(nodes, id) {
  for (const node of nodes) {
    if (node.id === id) return node;
    if (node.children) {
      const found = findNodeById(node.children, id);
      if (found) return found;
    }
  }
  return null;
}


  const handleFileLoad = (text) => setTreeText(text);

  const handleFocus = (node) => setFocusedNodeId(node);
  const handleUnfocus = () => setFocusedNodeId(null);

  const focused = focusedNodeId ? findNodeById(treeData, focusedNodeId) : null;
  const displayedTree = focused ? [focused] : treeData;


  return (
    <div className="App" style={{ padding: "1rem" }}>
      <h2>McGinty Family Tree Editor</h2>
      <div style={{ display: "flex", gap: "1rem" }}>
        <div style={{ flex: 1 }}>
          <UploadButton onLoad={handleFileLoad} />
          <textarea
            value={treeText}
            onChange={(e) => setTreeText(e.target.value)}
            rows={30}
            style={{ width: "100%" }}
          />
        </div>
        <div style={{ flex: 1 }}>
          <TreeView
            tree={displayedTree}
            onFocus={handleFocus}
            onUnfocus={handleUnfocus}
            focusedNodeId={focused ? focused.id : null}
          />
        </div>
        <div style={{ flex: 1 }}>
          <GraphView tree={displayedTree} />
        </div>
      </div>
      <DownloadButtons
        treeData={treeData}
        treeText={treeText}
        htmlContent={generateHTML(treeData)}
      />
    </div>
  );
};

export default App;
