// TreeView.js
import React, { useState } from "react";
import { FaSearchPlus, FaSearchMinus } from "react-icons/fa";

const Row = ({ children, level }) => (
  <div
    style={{
      display: "flex",
      alignItems: "center",
      gap: 8,
      marginLeft: level * 20,
      lineHeight: 1.4,
      padding: "2px 0",
      whiteSpace: "nowrap",
    }}
  >
    {children}
  </div>
);

const Toggle = ({ onClick, isExpanded, visible }) => {
  if (!visible) return <span style={{ width: 16 }} />; // spacer
  return (
    <span
      onClick={onClick}
      title={isExpanded ? "Collapse" : "Expand"}
      style={{ cursor: "pointer", width: 16, display: "inline-block", userSelect: "none" }}
    >
      {isExpanded ? ">" : "v"}
    </span>
  );
};

const TreeNode = ({
  node,
  level = 0,
  onFocus,
  onUnfocus,
  focusedNodeId,
  expandedNodes,
  toggleNode,
}) => {
  const hasChildren = Array.isArray(node.children) && node.children.length > 0;
  const isFocused = focusedNodeId === node.id;
  const isExpanded = expandedNodes[node.id] ?? true;

  return (
    <div>
      <Row level={level}>
        <Toggle
          visible={hasChildren}
          isExpanded={isExpanded}
          onClick={() => toggleNode(node.id ?? node.name)}
        />

        {/* the text grows, keeping the icon tight to the right of it */}
        <span style={{ flex: 0 }}>{node.name}</span>

        <button
          title={isFocused ? "Unfocus" : "Focus"}
          onClick={() => (isFocused ? onUnfocus() : onFocus(node))}
          style={{
            background: "none",
            border: "none",
            cursor: "pointer",
            padding: 0,
            marginLeft: 8,
            opacity: 0.8,
          }}
        >
          {isFocused ? <FaSearchMinus /> : <FaSearchPlus />}
        </button>
      </Row>

      {/* IMPORTANT: show children even when focused; siblings are hidden by App by passing [focusedNode] */}
      {hasChildren && isExpanded && (
        <div>
          {node.children.map((child, idx) => (
            <TreeNode
              key={child.id ?? `${node.id ?? node.name}-${idx}`}
              node={child}
              level={level + 1}
              onFocus={onFocus}
              onUnfocus={onUnfocus}
              focusedNodeId={focusedNodeId}
              expandedNodes={expandedNodes}
              toggleNode={toggleNode}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default function TreeView({ tree, onFocus, onUnfocus, focusedNodeId }) {
  const [expandedNodes, setExpandedNodes] = useState({});

  const toggleNode = (id) => {
    const safeId = id ?? Math.random().toString(36).slice(2);
    setExpandedNodes((prev) => ({ ...prev, [safeId]: !prev[safeId] }));
  };

  if (!tree) return <div>No tree data available</div>;
  const nodes = Array.isArray(tree) ? tree : [tree];

  return (
    <div style={{ padding: 10, minWidth: 600 }}>
      {nodes.map((node, idx) => (
        <TreeNode
          key={node.id ?? `${node.name}-${idx}`}
          node={node}
          level={0}
          onFocus={onFocus}
          onUnfocus={onUnfocus}
          focusedNodeId={focusedNodeId}
          expandedNodes={expandedNodes}
          toggleNode={toggleNode}
        />
      ))}
    </div>
  );
}
