import React from "react";
import { FaSearchPlus, FaSearchMinus } from "react-icons/fa";

const TreeNode = ({ node, onFocus, onUnfocus, focusedNodeId, level = 0 }) => {
  const isFocused = focusedNodeId === node.id;

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        marginLeft: `${level * 40}px`,
        position: "relative",
      }}
    >
      <div style={{ display: "flex", alignItems: "center", position: "relative" }}>
        <div
          style={{
            backgroundColor: "#f5f5f5",
            padding: "4px 8px",
            borderRadius: "6px",
            whiteSpace: "pre-wrap",
            flexGrow: 1,
            border: "1px solid #ccc",
          }}
        >
          {node.name}
        </div>
        <div style={{ marginLeft: "6px" }}>
          {isFocused ? (
            <button
              onClick={onUnfocus}
              title="Unfocus"
              style={{
                background: "none",
                border: "none",
                cursor: "pointer",
              }}
            >
              <FaSearchMinus />
            </button>
          ) : (
            <button
              onClick={() => onFocus(node)}
              title="Focus"
              style={{
                background: "none",
                border: "none",
                cursor: "pointer",
              }}
            >
              <FaSearchPlus />
            </button>
          )}
        </div>
      </div>
      {node.children &&
        node.children.map((child) => (
          <div
            key={child.id}
            style={{
              borderLeft: "2px solid #ccc",
              marginLeft: "8px",
              paddingLeft: "8px",
            }}
          >
            <TreeNode
              node={child}
              onFocus={onFocus}
              onUnfocus={onUnfocus}
              focusedNodeId={focusedNodeId}
              level={level + 1}
            />
          </div>
        ))}
    </div>
  );
};

export default function TreeView({ tree, onFocus, onUnfocus, focusedNodeId }) {
  return (
    <div>
      {Array.isArray(tree) ? (
        tree.map((node) => (
          <TreeNode
            key={node.id}
            node={node}
            onFocus={onFocus}
            onUnfocus={onUnfocus}
            focusedNodeId={focusedNodeId}
          />
        ))
      ) : (
        <TreeNode
          node={tree}
          onFocus={onFocus}
          onUnfocus={onUnfocus}
          focusedNodeId={focusedNodeId}
        />
      )}
    </div>
  );
}
