// TreeEditor.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { parseTree } from "./utils/parseTree";
import "./TreeEditor.css";

const DEBOUNCE_MS = 150;

export default function TreeEditor({ treeText, onTextChange }) {
  const [draft, setDraft] = useState(treeText || "");
  const [wrap, setWrap] = useState(true);
  const [errors, setErrors] = useState([]);
  const taRef = useRef(null);
  const gutterRef = useRef(null);
  const scrollSync = () => {
    if (!taRef.current || !gutterRef.current) return;
    gutterRef.current.scrollTop = taRef.current.scrollTop;
  };

  // keep local draft in sync when parent loads a file
  useEffect(() => {
    setDraft(treeText || "");
  }, [treeText]);
  // NEW: keep gutter height in sync with textarea height
  useEffect(() => {
    const ta = taRef.current;
    const gutter = gutterRef.current;
    if (!ta || !gutter) return;

    const apply = () => {
      const h = ta.clientHeight;
      gutter.style.height = `${h}px`;
    };

    // run once
    apply();

    // update on resize
    const ro = new ResizeObserver(apply);
    ro.observe(ta);

    return () => ro.disconnect();
  }, []);
  // debounce changes up to parent
  useEffect(() => {
    const t = setTimeout(() => {
      onTextChange?.(draft);
      // quick validation on idle
      try {
        parseTree(draft);
        setErrors([]);
      } catch (e) {
        setErrors([String(e?.message || e)]);
      }
    }, DEBOUNCE_MS);
    return () => clearTimeout(t);
  }, [draft, onTextChange]);

  // computed line numbers
  const lines = useMemo(() => draft.split("\n").length || 1, [draft]);
  const lineNumbers = useMemo(
    () => Array.from({ length: lines }, (_, i) => i + 1).join("\n"),
    [lines]
  );

  // handle Tab / Shift+Tab and auto-indent on Enter
  const handleKeyDown = (e) => {
    const ta = taRef.current;
    if (!ta) return;

    // Tab inserts a tab character (or 2 spaces if you prefer)
    if (e.key === "Tab") {
      e.preventDefault();
      const { selectionStart, selectionEnd, value } = ta;
      const insert = "\t";
      const next = value.slice(0, selectionStart) + insert + value.slice(selectionEnd);
      const caret = selectionStart + insert.length;
      setDraft(next);
      requestAnimationFrame(() => {
        ta.selectionStart = ta.selectionEnd = caret;
      });
      return;
    }

    // Auto-indent on Enter: copy leading whitespace of current line
    if (e.key === "Enter") {
      const { selectionStart, selectionEnd, value } = ta;
      if (selectionStart !== selectionEnd) return; // let default handle multi-line selection
      const before = value.slice(0, selectionStart);
      const after = value.slice(selectionStart);
      const lineStart = before.lastIndexOf("\n") + 1;
      const currentLine = before.slice(lineStart);
      const leadingWS = currentLine.match(/^[\t ]*/)?.[0] ?? "";
      const next = before + "\n" + leadingWS + after;
      e.preventDefault();
      setDraft(next);
      requestAnimationFrame(() => {
        const pos = selectionStart + 1 + leadingWS.length;
        ta.selectionStart = ta.selectionEnd = pos;
      });
    }
  };

  return (
    <div className="te-root">
      <div className="te-toolbar">
        <label className="te-wrap">
          <input
            type="checkbox"
            checked={wrap}
            onChange={(e) => setWrap(e.target.checked)}
          />
          Soft wrap
        </label>
        {errors.length > 0 ? (
          <span className="te-status te-status-error">Parse issues</span>
        ) : draft.length ? (
          <span className="te-status te-status-ok">OK</span>
        ) : null}
      </div>

      <div className="te-shell">
        <pre
          className="te-gutter"
          ref={gutterRef}
          aria-hidden="true"
        >
{lineNumbers}
        </pre>

        <textarea
          ref={taRef}
          className="te-textarea"
          value={draft}
          onChange={(e) => setDraft(e.target.value)}
          onKeyDown={handleKeyDown}
          onScroll={scrollSync}
          spellCheck={false}
          wrap={wrap ? "soft" : "off"}
          placeholder="Paste or edit your tab-indented tree hereâ€¦"
        />
      </div>

      {errors.length > 0 && (
        <div className="te-errors" role="alert">
          <strong>Validation:</strong>
          <ul>
            {errors.map((err, i) => (
              <li key={i}>{err}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
