import React, { useState } from "react";
import { FaSearchPlus, FaSearchMinus } from "react-icons/fa";

const TreeNode = ({ node, onFocus, onUnFocus, focusedNodeId, depth = 0 }) => {
  const [collapsed, setCollapsed] = useState(true);
  const isFocused = focusedNodeId && focusedNodeId === node.id;

  const paddingLeft = `${depth * 20}px`;

  return (
    <li key={node.id || node.name} style={{ listStyleType: "none", paddingLeft }}>
      <div style={{ display: "flex", alignItems: "center", gap: "6px" }}>
        {node.children?.length > 0 && (
          <button
            onClick={() => setCollapsed(!collapsed)}
            style={{
              background: "none",
              border: "none",
              fontWeight: "bold",
              cursor: "pointer"
            }}
            title={collapsed ? "Expand" : "Collapse"}
          >
            {collapsed ? "▶" : "▼"}
          </button>
        )}
        <span style={{ fontWeight: "bold" }}>{node.name}</span>
        <span style={{ marginLeft: "auto", display: "flex", gap: "6px" }}>
          {!isFocused && (
            <button
              onClick={() => onFocus(node)}
              style={{ background: "none", border: "none", cursor: "pointer" }}
              title="Focus branch"
            >
              <FaSearchPlus />
            </button>
          )}
          {isFocused && (
            <button
              onClick={onUnFocus}
              style={{ background: "none", border: "none", cursor: "pointer" }}
              title="Unfocus branch"
            >
              <FaSearchMinus />
            </button>
          )}
        </span>
      </div>

      {!collapsed && node.children?.length > 0 && (
        <ul style={{ paddingLeft: "0px" }}>
          {node.children.map((child) => (
            <TreeNode
              key={child.id || child.name}
              node={child}
              onFocus={onFocus}
              onUnFocus={onUnFocus}
              focusedNodeId={focusedNodeId}
              depth={depth + 1}
            />
          ))}
        </ul>
      )}
    </li>
  );
};

const TreeView = ({ tree, onFocus, onUnFocus, focusedNodeId }) => {
  if (!tree || tree.length === 0) {
    return <div>No tree data available</div>;
  }

  const nodes = Array.isArray(tree) ? tree : [tree];

  return (
    <div style={{ padding: "1rem", maxWidth: "100%" }}>
      <h3>Tree View (Collapsible)</h3>
      <ul style={{ padding: 0, margin: 0 }}>
        {nodes.map((node) => (
          <TreeNode
            key={node.id || node.name}
            node={node}
            onFocus={onFocus}
            onUnFocus={onUnFocus}
            focusedNodeId={focusedNodeId}
          />
        ))}
      </ul>
    </div>
  );
};

export default TreeView;
