import React, { useState, useEffect } from "react";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import DownloadButtons from "./DownloadButtons";
import TreeEditor from "./TreeEditor";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";
import "./App.css";

function App() {
  const [treeText, setTreeText] = useState([]);
  const [treeData, setTreeData] = useState([]);
  const [focusedNodeId, setFocusedNodeId] = useState(null);

  const displayedTree = pinnedId
    ? filterTree(treeData, pinnedId)
    : treeData;


  useEffect(() => {
    try {
      const parsed = parseTree(treeText);
      setTreeData(parsed);
    } catch (err) {
      console.error("Failed to parse tree:", err);
    }
  }, [treeText]);

    const handleTextChange = (e) => setTreeText(e.target.value);
    const handleFileLoad = (text) => setTreeText(text);
    const handleFocus = (node) => setPinnedId(node.id);
    const handleUnFocus = () => setPinnedId(null);

  const handleDownloadHTML = () => {
    const html = generateHTML(displayedTree);
    const blob = new Blob([html], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "family_tree.html";
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleDownloadJSON = () => {
    const json = JSON.stringify(displayedTree, null, 2);
    const blob = new Blob([json], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "family_tree.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="App">
      <div className="top-bar">
        <UploadButton onLoad={handleFileLoad} />
        <button onClick={handleUnfocus} disabled={!focusedNodeId}>
          Unfocus
        </button>
        <DownloadButtons
          tree={treeData}
          treeText={treeText}
          onDownloadHTML={handleDownloadHTML}
          onDownloadJSON={handleDownloadJSON}
          onDownloadSVG={() => {}}
        />
      </div>
      <div className="main-content">
        <div className="pane">
          <TreeEditor treeText={treeText} setTreeText={setTreeText} />
        </div>
        <div className="pane">
          <TreeView
            tree={displayedTree}
            onFocus={handleFocus}
            onUnFocus={handleUnFocus}
            focusedNodeId={pinnedId}
          />
          <GraphView tree={displayedTree} />
        </div>
      </div>
    </div>
  );
}

function filterTree(tree, id) {
  for (const node of tree) {
    if (node.id === id) return [node];
    if (node.children) {
      const match = filterTree(node.children, id);
      if (match.length > 0) return match;
    }
  }
  return [];
}

export default App;
