// App.js
import React, { useState, useEffect } from "react";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import DownloadButton from "./DownloadButton";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";
import "./App.css";

function App() {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState([]);
  const [displayedTree, setDisplayedTree] = useState([]);
  const [focusedNodeId, setFocusedNodeId] = useState(null);

  // Re-parse tree text when it changes
  useEffect(() => {
    try {
      const parsed = parseTree(treeText);
      setTreeData(parsed);
      setDisplayedTree(parsed);
    } catch (err) {
      console.error("Failed to parse tree:", err);
    }
  }, [treeText]);

  const handleTextChange = (e) => {
    setTreeText(e.target.value);
  };

  const handleFileLoad = (text) => {
    setTreeText(text);
  };
  const handleFocus = (node) => {
    console.log("Focusing node:", node);
    setFocusedNodeId(node.id);
  };

  const handleUnfocus = () => {
    setFocusedNodeId(null);
  };

  const handleDownloadHTML = () => {
    const html = generateHTML(displayedTree);
    const blob = new Blob([html], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "family_tree.html";
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleDownloadJSON = () => {
    const json = JSON.stringify(displayedTree, null, 2);
    const blob = new Blob([json], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "family_tree.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="app-container">
      <h1>McGinty Family Tree Editor</h1>
      <UploadButton onLoad={handleFileLoad} />
      <div className="editor-container">
        <div className="editor-pane">
          <h3>Tree Text Editor</h3>
          <textarea
            value={treeText}
            onChange={handleTextChange}
            placeholder="Paste or edit tree text here..."
            rows={30}
            cols={40}
          />
        </div>
        <div className="tree-pane">
          <TreeView
            tree={displayedTree}
            onFocus={handleFocus}
            onUnfocus={handleUnfocus}
            focusedNodeId={focusedNodeId}
          />
          <GraphView tree={displayedTree} />
        </div>
      </div>
      <div className="button-row">
        {focusedNodeId && (
          <button onClick={handleUnfocus}>Unfocus</button>
        )}
        <DownloadButton onClick={handleDownloadHTML} label="Download HTML" />
        <DownloadButton onClick={handleDownloadJSON} label="Download JSON" />
      </div>
    </div>
  );
}

export default App;
