import React, { useState, useEffect } from "react";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import DownloadButtons from "./DownloadButtons";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";

function App() {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState([]);
  const [focusedNode, setFocusedNode] = useState(null);

  // Parse tree text into JSON structure
  useEffect(() => {
    try {
      const parsed = parseTree(treeText);
      setTreeData(parsed);
    } catch (err) {
      console.error("Parse error:", err);
      setTreeData([]);
    }
  }, [treeText]);

  const displayedTree = focusedNode ? focusedNode : treeData;

  const handleFileLoad = (text) => {
    setTreeText(text);
  };

  const handleTextChange = (e) => {
    setTreeText(e.target.value);
  };

  const handleFocus = (node) => setFocusedNode(node);
  const handleUnfocus = () => setFocusedNode(null);

  function downloadFile(filename, content, type) {
    const blob = new Blob([content], { type });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  }


  const handleDownloadHTML = () => {
    if (!treeData || treeData.length === 0) return;
    const htmlContent = generateHTML(treeData);
    downloadFile("tree.html", htmlContent, "text/html");
  };

  const handleDownloadJSON = () => {
    if (!treeData || treeData.length === 0) return;
    const jsonContent = JSON.stringify(treeData, null, 2);
    downloadFile("tree.json", jsonContent, "application/json");
  };

  return (
    <div className="App" style={{ padding: "1rem" }}>
      <div style={{ display: "flex", gap: "1rem" }}>
        <div style={{ flex: 1 }}>
          <h3>Tree Text Editor</h3>
          <textarea
            value={treeText}
            onChange={handleTextChange}
            rows={25}
            cols={50}
          />
          <br />
          <UploadButton onLoad={handleFileLoad} />

          <DownloadButtons
            onDownloadHTML={handleDownloadHTML}
            onDownloadSVG={() => {/* optional: wire up later */}}
            onDownloadJSON={handleDownloadJSON}
            />
        </div>

        <div style={{ flex: 1 }}>
          <TreeView
            tree={displayedTree}
            onFocus={handleFocus}
            onUnfocus={handleUnfocus}
            focusedNodeId={focusedNode ? focusedNode.id : null}
          />

          {focusedNode && (
            <button onClick={handleUnfocus} style={{ marginTop: "1rem" }}>
              Unfocus
            </button>
          )}
        </div>

        <div style={{ flex: 1 }}>
          <GraphView tree={displayedTree} />
        </div>
      </div>
    </div>
  );
}

export default App;
