// DownloadButtons.js
import React from "react";

const downloadSVG = () => {
  const svgElement = document.querySelector("svg");
  if (!svgElement) return;

  const serializer = new XMLSerializer();
  let source = serializer.serializeToString(svgElement);

  // Add SVG namespace if missing
  if (!source.match(/^<svg[^>]+xmlns="http:\/\/www\.w3\.org\/2000\/svg"/)) {
    source = source.replace(
      /^<svg/,
      '<svg xmlns="http://www.w3.org/2000/svg"'
    );
  }

  const blob = new Blob([source], { type: "image/svg+xml;charset=utf-8" });
  const url = URL.createObjectURL(blob);

  const link = document.createElement("a");
  link.href = url;
  link.download = "tree_diagram.svg";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
  URL.revokeObjectURL(url);
};


const DownloadButtons = ({ onDownloadHTML, onDownloadSVG, onDownloadJSON }) => {
  return (
    <div style={{ marginTop: "1em" }}>
      <button onClick={onDownloadHTML}>Download HTML</button>
      <button onClick={downloadSVG}>Download SVG</button>
      <button onClick={onDownloadJSON}>Download JSON</button>
    </div>
  );
};

export default DownloadButtons;
