// TreeView.js
import React from "react";
import { FaSearchPlus, FaSearchMinus } from "react-icons/fa";

const rowStyle = {
  display: "flex",
  alignItems: "center",
  gap: 8,
  padding: "2px 0",
  whiteSpace: "nowrap",
};

const connectorStyle = {
  display: "inline-block",
  width: 20,
  height: 1,
  backgroundColor: "#999",
  marginRight: 5,
};

function TreeNode({ node, level = 0, onFocus, onUnfocus, focusedNodeId }) {
  const hasChildren = Array.isArray(node.children) && node.children.length > 0;
  const isFocused = focusedNodeId === node.id;

  return (
    <div style={{ marginLeft: level * 20 }}>
      <div style={rowStyle}>
        {hasChildren && <span style={connectorStyle} />}
        <span>{node.name}</span>
        <button
          title={isFocused ? "Unfocus" : "Focus"}
          onClick={() => (isFocused ? onUnfocus() : onFocus(node))}
          style={{
            background: "none",
            border: "none",
            cursor: "pointer",
            marginLeft: "auto",
            color: "#555",
          }}
        >
          {isFocused ? <FaSearchMinus /> : <FaSearchPlus />}
        </button>
      </div>

      {hasChildren && !isFocused && (
        <div>
          {node.children.map((child) => (
            <TreeNode
              key={child.id ?? `${node.id ?? node.name}-${child.name}`}
              node={child}
              level={level + 1}
              onFocus={onFocus}
              onUnfocus={onUnfocus}
              focusedNodeId={focusedNodeId}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default function TreeView({ tree, onFocus, onUnfocus, focusedNodeId }) {
  if (!tree) return <div>No tree data available</div>;
  const nodes = Array.isArray(tree) ? tree : [tree];

  return (
    <div style={{ padding: 10, minWidth: 600 }}>
      {nodes.map((n) => (
        <TreeNode
          key={n.id ?? n.name}
          node={n}
          level={0}
          onFocus={onFocus}
          onUnfocus={onUnfocus}
          focusedNodeId={focusedNodeId}
        />
      ))}
    </div>
  );
}
