// TreeView.js
import React, { useState } from "react";

const TreeNode = ({ node, onFocus, focusedNodeId }) => {
  const [collapsed, setCollapsed] = useState(false);

  const hasChildren = node.children && node.children.length > 0;

  const toggleCollapse = () => {
    setCollapsed(!collapsed);
  };

  return (
    <li>
      <span onClick={toggleCollapse} style={{ cursor: hasChildren ? "pointer" : "default", fontWeight: "bold" }}>
        {hasChildren ? (collapsed ? "▸" : "▾") : "•"} {node.name}
      </span>
      <button style={{ marginLeft: "10px" }} onClick={() => onFocus(node.id)}>
        Focus
      </button>
      {hasChildren && !collapsed && (
        <ul>
          {node.children.map((child, index) => (
            <TreeNode key={child.id || index} node={child} onFocus={onFocus} focusedNodeId={focusedNodeId} />
          ))}
        </ul>
      )}
    </li>
  );
};

const TreeView = ({ tree, onFocus, focusedNodeId }) => {
  if (!tree || tree.length === 0) {
    return <div>No tree data available</div>;
  }

  return (
    <div>
      <h3>Tree View (Collapsible)</h3>
      <ul>
        {tree.map((node, index) => (
          <TreeNode key={node.id || index} node={node} onFocus={onFocus} focusedNodeId={focusedNodeId} />
        ))}
      </ul>
    </div>
  );
};

export default TreeView;
