import React, { useState } from "react";

const TreeNode = ({ node, onFocus, onUnFocus, focusedNodeId }) => {
  const [collapsed, setCollapsed] = useState(true);

  const isFocused = focusedNodeId && focusedNodeId === node.id;

  return (
    <li key={node.id || node.name}>
      <div>
        {node.children && node.children.length > 0 && (
          <button onClick={() => setCollapsed(!collapsed)}>
            {collapsed ? "+" : "-"}
          </button>
        )}
        <strong>{node.name}</strong>
        <button onClick={() => onFocus(node)}>Focus</button>
        {isFocused && <button onClick={onUnFocus}>Unfocus</button>}
      </div>

      {!collapsed && node.children && node.children.length > 0 && (
        <ul>
          {node.children.map((child) => (
            <TreeNode
              key={child.id || child.name}
              node={child}
              onFocus={onFocus}
              onUnFocus={onUnFocus}
              focusedNodeId={focusedNodeId}
            />
          ))}
        </ul>
      )}
    </li>
  );
};

const TreeView = ({ tree, onFocus, onUnFocus, focusedNodeId }) => {
  if (!tree || tree.length === 0) {
    return <div>No tree data available</div>;
  }

  // tree can be a single object or an array
  const nodes = Array.isArray(tree) ? tree : [tree];

  return (
    <div>
      <h3>Tree View (Collapsible)</h3>
      <ul>
        {nodes.map((node) => (
          <TreeNode
            key={node.id || node.name}
            node={node}
            onFocus={onFocus}
            onUnFocus={onUnFocus}
            focusedNodeId={focusedNodeId}
          />
        ))}
      </ul>
    </div>
  );
};

export default TreeView;
