// App.js
import React, { useState } from "react";
import TreeEditor from "./TreeEditor";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";
import "./App.css";

function App() {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState(null);
  const [displayedTree, setDisplayedTree] = useState(null);
  const [focusedNode, setFocusedNode] = useState(null);

  const handleFileLoad = (text) => {
    setTreeText(text);
    try {
      const parsed = parseTree(text);
      setTreeData(parsed);
      setDisplayedTree(parsed);
      setFocusedNode(null);
      console.log("Parsed tree:", parsed);
    } catch (error) {
      console.error("Failed to parse tree:", error);
    }
  };

  const handleFocus = (node) => {
    if (!node) {
      setFocusedNode(null);
      setDisplayedTree(treeData);
    } else {
      setFocusedNode(node);
      setDisplayedTree([node]);
    }
  };

  return (
    <div className="app-container">
      <h1>McGinty Family Tree Editor</h1>
      <UploadButton onLoad={handleFileLoad} />
      <div className="editor-view">
        <TreeEditor treeText={treeText} setTreeText={setTreeText} />
        <div className="tree-panel">
          {displayedTree && (
            <>
              <TreeView tree={displayedTree} onFocus={handleFocus} />
              <GraphView tree={displayedTree} />
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
