// App.js
import React, { useEffect, useState } from "react";
import UploadButton from "./UploadButton";
import DownloadButtons from "./DownloadButtons";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";

function findNodeById(nodes, id) {
  for (const n of nodes) {
    if ((n.id ?? n.name) === id) return n;
    if (Array.isArray(n.children)) {
      const found = findNodeById(n.children, id);
      if (found) return found;
    }
  }
  return null;
}

export default function App() {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState([]);
  const [focusedNodeId, setFocusedNodeId] = useState(null);
  // Width of the left editor pane as a percentage
  const [leftPct, setLeftPct] = useState(40);
  const draggingRef = useRef(false);

  useEffect(() => {
    const onMove = (e) => {
      if (!draggingRef.current) return;
      const app = document.querySelector(".app-root");
      if (!app) return;
      const rect = app.getBoundingClientRect();
      const x = e.clientX - rect.left; // pixels from left of container
      const pct = Math.min(70, Math.max(20, (x / rect.width) * 100)); // clamp 20–70%
      setLeftPct(pct);
    };
    const onUp = () => (draggingRef.current = false);

    window.addEventListener("mousemove", onMove);
    window.addEventListener("mouseup", onUp);
    return () => {
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("mouseup", onUp);
    };
  }, []);

  const startDrag = () => (draggingRef.current = true);
  // parse on text change
  useEffect(() => {
    try {
      const parsed = parseTree(treeText); // must return an ARRAY of root nodes
      setTreeData(Array.isArray(parsed) ? parsed : (parsed ? [parsed] : []));
    } catch (e) {
      console.error("Parse error:", e);
      setTreeData([]);
    }
  }, [treeText]);

  // file load
  const handleFileLoad = (text) => setTreeText(text);

  // focus/unfocus
  const handleFocus = (node) => setFocusedNodeId(node.id ?? node.name);
  const handleUnfocus = () => setFocusedNodeId(null);

  // compute displayed tree
  const focusedNode = focusedNodeId ? findNodeById(treeData, focusedNodeId) : null;
  const displayedTree = focusedNode ? [focusedNode] : treeData;

  // downloads
  const download = (content, name, type) => {
    const blob = new Blob([content], { type });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = name;
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleDownloadHTML = () => {
    const html = generateHTML(displayedTree);
    download(html, "family_tree.html", "text/html");
  };

  const handleDownloadJSON = () => {
    const json = JSON.stringify(displayedTree, null, 2);
    download(json, "family_tree.json", "application/json");
  };

  const handleDownloadSVG = () => {
    const svg = document.getElementById("graph-svg");
    if (!svg) return;
    const serializer = new XMLSerializer();
    // include xmlns so standalone SVG opens correctly
    const clone = svg.cloneNode(true);
    clone.setAttribute("xmlns", "http://www.w3.org/2000/svg");
    const source = serializer.serializeToString(clone);
    download(source, "family_tree.svg", "image/svg+xml");
  };

  return (
  <div className="app-root">
      {/* Top controls, keep whatever you already have */}
      <div className="top-bar">
        <UploadButton onLoad={handleFileLoad} />
        {focusedNode && <button onClick={handleUnfocus}>Unfocus</button>}
        <DownloadButtons
          onDownloadHTML={handleDownloadHTML}
          onDownloadSVG={handleDownloadSVG}
          onDownloadJSON={handleDownloadJSON}
        />
      </div>

      {/* Two-pane row with draggable gutter */}
      <div className="two-pane">
        <div className="pane left" style={{ width: `${leftPct}%` }}>
          <h3>Tree Text Editor</h3>
          <textarea
            value={treeText}
            onChange={handleTextChange}
            placeholder="Paste or edit tree text here..."
          />
        </div>

        <div
          className="gutter"
          role="separator"
          aria-orientation="vertical"
          onMouseDown={startDrag}
          title="Drag to resize"
        />

        <div className="pane right">
          <TreeView
            tree={displayedTree}
            onFocus={handleFocus}
            onUnfocus={handleUnfocus}
            focusedNodeId={focusedNode ? focusedNode.id : null}
            expandedNodes={expandedNodes}
            toggleNode={toggleNode}
          />
        </div>
      </div>
      {/* SVG view below */}
      <div className="svg-row">
        <GraphView tree={displayedTree} />
      </div>
    </div>
  );
}
