import React from "react";
import { FaSearchPlus, FaSearchMinus } from "react-icons/fa";
import "./TreeView.css";

const TreeNode = ({ node, level = 0, onFocus, onUnfocus, focusedNodeId }) => {
  const hasChildren = node.children && node.children.length > 0;
  const isFocused = node.id === focusedNodeId;

  return (
    <div className="tree-node" style={{ marginLeft: `${level * 20}px` }}>
      <div className="tree-node-line">
        {hasChildren && <span className="tree-node-connector" />}
        <span>{node.name}</span>

        <button
          className="icon-button"
          onClick={() => (isFocused ? onUnfocus() : onFocus(node))}
          title={isFocused ? "Unfocus" : "Focus"}
        >
          {isFocused ? <FaSearchMinus /> : <FaSearchPlus />}
        </button>
      </div>

      {hasChildren && !isFocused && (
        <div className="tree-children">
          {node.children.map((child) => (
            <TreeNode
              key={child.id}
              node={child}
              level={level + 1}
              onFocus={onFocus}
              onUnfocus={onUnfocus}
              focusedNodeId={focusedNodeId}
            />
          ))}
        </div>
      )}
    </div>
  );
};

const TreeView = ({ tree, onFocus, onUnfocus, focusedNodeId }) => {
  if (!Array.isArray(tree)) return <div>No tree data available</div>;

  return (
    <div className="tree-view">
      {tree.map((node) => (
        <TreeNode
          key={node.id}
          node={node}
          level={0}
          onFocus={onFocus}
          onUnfocus={onUnfocus}
          focusedNodeId={focusedNodeId}
        />
      ))}
    </div>
  );
};

export default TreeView;
