export function generateHTML(treeData) {
  const renderNode = (node) => {
    const children = node.children
      ? `<ul>${node.children.map(renderNode).join("")}</ul>`
      : "";
    return `<li>${node.name}${children}</li>`;
  };

  const body =
    Array.isArray(treeData) && treeData.length > 0
      ? `<ul>${treeData.map(renderNode).join("")}</ul>`
      : "<p>No data</p>";

  return `
    <!DOCTYPE html>
    <html>
      <head>
        <title>Tree</title>
        <style>
          ul { list-style-type: none; }
          li { margin: 4px 0; }
        </style>
      </head>
      <body>
        ${body}
      </body>
    </html>
  `;
}
