import React, { useState, useEffect } from "react";
import TreeEditor from "./TreeEditor";
import TreeView from "./TreeView";
import GraphView from "./GraphView";
import UploadButton from "./UploadButton";
import DownloadButton from "./DownloadButton";
import { parseTree } from "./utils/parseTree";
import { generateHTML } from "./utils/generateHTML";
import "./App.css";

const App = () => {
  const [treeText, setTreeText] = useState("");
  const [treeData, setTreeData] = useState(null);
  const [focusedNode, setFocusedNode] = useState(null);

  useEffect(() => {
    try {
      const parsed = parseTree(treeText);
      setTreeData(parsed);
    } catch (err) {
      console.error("Error parsing tree:", err);
      setTreeData(null);
    }
  }, [treeText]);

  const handleFileLoad = (text) => {
    setTreeText(text);
  };

  const handleTextChange = (text) => {
    setTreeText(text);
  };

  const handleFocus = (node) => {
    setFocusedNode(node);
  };

  const handleUnfocus = () => {
    setFocusedNode(null);
  };

  const displayedTree = focusedNode ? [focusedNode] : treeData;

  return (
    <div className="app-container">
      <div className="header">
        <h1>McGinty Family Tree Editor</h1>
        <UploadButton onLoad={handleFileLoad} />
        <DownloadButton
          treeData={treeData}
          treeText={treeText}
          generateHTML={() => generateHTML(treeData)}
        />
        {focusedNode && <button onClick={handleUnfocus}>Unfocus</button>}
      </div>
      <div className="main-content">
        <div className="editor-pane">
          <TreeEditor treeText={treeText} onTextChange={handleTextChange} />
        </div>
        <div className="tree-pane">
          <TreeView tree={displayedTree} onFocus={handleFocus} />
          <GraphView tree={displayedTree} />
        </div>
      </div>
    </div>
  );
};

export default App;
